
import { Component, ReactNode } from 'react';

export type CheckBoxGroupTypeFieldType = 'normal' | 'button' | 'line';
export type CheckBoxGroupValueFieldType = string | number;
export type CheckBoxGroupWidthFieldType = 'default' | 'available';
export type CheckBoxGroupThemeFieldType = 'alfa-on-color' | 'alfa-on-white';


export interface CheckBoxGroupProps {

    /**
     * Тип компонента
     */
    type?: CheckBoxGroupTypeFieldType;
    /**
     * Выбранные чекбокс-кнопки
     */
    value?: CheckBoxGroupValueFieldType[];
    /**
     * Отображение попапа с ошибкой в момент когда фокус находится на компоненте
     */
    width?: CheckBoxGroupWidthFieldType;
    /**
     * Уникальное имя блока
     */
    name?: string;
    /**
     * Управление возможностью изменения состояния 'checked' дочерних компонентов `CheckBox`
     */
    disabled?: boolean;
    /**
     * Дочерние элементы `CheckBoxGroup`, как правило, компоненты `CheckBox`
     */
    children?: ReactNode;
    /**
     * Тема компонента
     */
    theme?: CheckBoxGroupThemeFieldType;
    /**
     * Дополнительный класс
     */
    className?: string;
    /**
     * Идентификатор компонента в DOM
     */
    id?: string;
    /**
     * Лейбл для группы
     */
    label?: ReactNode;
    /**
     * Обработчик фокуса радиогруппы
     */
    onFocus?: Function;
    /**
     * Обработчик снятия фокуса с радиогруппы
     */
    onBlur?: Function;
    /**
     * Обработчик изменения значения 'checked' одного из дочерних радио-кнопок
     */
    onChange?: Function;
}



/**
 * Компонент группы чекбоксов.
 */

export default class CheckBoxGroup extends Component<CheckBoxGroupProps, any> {

    /**
     * Устанавливает фокус на первую чекбокс-кнопку в группе.
     */
    focus(): any;
    /**
     * Убирает фокус с группы чекбокс-кнопок.
     */
    blur(): any;
}
