
import { Component, ReactNode } from 'react';

export type FlagIconModeFieldType = 'chunk' | 'sprite';
export type FlagIconSizeFieldType = 's' | 'm' | 'l' | 'xl';
export type FlagIconThemeFieldType = 'alfa-on-color' | 'alfa-on-white';
export type FlagIconClassNameFieldType = Function | string;


export interface FlagIconProps {

    /**
     * Код страны из <a href="https://ru.wikipedia.org/wiki/ISO_3166-1_alpha-2" target="_blank">ISO 3166-1 alpha-2</a>
     */
    country?: string;
    /**
     * Режим отображения
     */
    mode?: FlagIconModeFieldType;
    /**
     * Управление наличием тени у компонента
     */
    isFlat?: boolean;
    /**
     * Дочерние элементы `FlagIcon`
     */
    children?: ReactNode;
    /**
     * Размер компонента
     */
    size?: FlagIconSizeFieldType;
    /**
     * Тема компонента
     */
    theme?: FlagIconThemeFieldType;
    /**
     * Дополнительный класс
     */
    className?: FlagIconClassNameFieldType;
}



/**
 * Компонент флага в виде иконки.
 */

export default class FlagIcon extends Component<FlagIconProps, any> {

}
