
import { Component, ReactNode } from 'react';

export type IconSizeFieldType = 'xs' | 's' | 'm' | 'l' | 'xl' | 'xxl';
export type IconThemeFieldType = 'alfa-on-color' | 'alfa-on-white';


export interface IconProps {

    /**
     * Дополнительный класс
     */
    className?: string;
    /**
     * Управление цветностью иконки
     */
    colored?: boolean;
    /**
     * Идентификатор компонента в DOM
     */
    id?: string;
    /**
     * Название иконки
     */
    name?: string;
    /**
     * Размер иконки
     */
    size?: IconSizeFieldType;
    /**
     * Тема компонента
     */
    theme?: IconThemeFieldType;
}



/**
 * Базовый компонент иконки. Содержит в себе только неодходимые для компонентов иконки.
 */

export default class Icon extends Component<IconProps, any> {

}
