
import { Component, ReactNode } from 'react';

export type SwipeableChildrenPropsFieldType = {
    onMouseDown?: Function;
    onTouchStart?: Function
};
export type SwipeableChildrenFieldType = {
    props?: SwipeableChildrenPropsFieldType
};


export interface SwipeableProps {

    /**
     * Число пикселей, на которое нужно сместиться, чтобы запустить функцию по свайпу
     */
    delta?: number;
    /**
     * Функция запускаемая по свайпу. Принимает в качестве аргумента направление свайпа ('top', 'right', 'bottom', 'left')
     */
    onSwipe: Function;
    /**
     * Дочерний компонент представленный в виде единичного элемента
     */
    children: SwipeableChildrenFieldType;
}



export default class Swipeable extends Component<SwipeableProps, any> {

}
